const fs = require('fs');

module.exports = function generateNewRelic(options={}) {
  const {licenseKey='', applicationID=''} = options;
  const newRelicText = [fs.readFileSync(`${__dirname}/new-relic.js`)];
  newRelicText.push(`;NREUM.info={beacon:"bam.nr-data.net",errorBeacon:"bam.nr-data.net",licenseKey:'${licenseKey}',applicationID:'${applicationID}',sa:1}`);
  return newRelicText.join('\n');
}