const { configForDeployTarget } = require('ticketfly-ember-env');

module.exports = function(environment, opts = {}) {
  const options = configForDeployTarget(opts.nrKeys);

  if (environment === 'test' || !options) {
    return {
      enabled: false
    };
  } else {
    options.enabled = true;
    return options;
  }
};
