const assert = require('assert');
const generateNewRelic = require('../lib/generate-new-relic');

describe('new relic script generation', function() {
  it('returns a new relic script', function() {
    const result = generateNewRelic();
    
    assert.ok(result);
  });

  it('returns a new relic script with correct license key and application id', function() {
    const newRelicScriptResult = (generateNewRelic({licenseKey: '12345678', applicationID: 'iuhiuhihia'})).split('\n')[2];
    
    assert.ok(newRelicScriptResult.indexOf('licenseKey:\'12345678\'') > -1);
    assert.ok(newRelicScriptResult.indexOf('applicationID:\'iuhiuhihia\'') > -1);
  });
})


