var fs          = require('fs');
var path        = require('path');

module.exports = {
  description: 'install ember-cli-foundation-6-sass into a typical project',
  normalizeEntityName: function() {},

  beforeInstall: function () {
    return this.addBowerPackageToProject('foundation-sites', '6.2.1');
  },

  afterInstall: function () {
    return this.addPackagesToProject([
      { name: 'ember-cli-sass', target: '^5.3.1' }
    ]).then(() => {
      return this.addAddonToProject({ name: 'ember-moment', target: '7.0.0-beta.3' });
    });
  }
};
