/* eslint-env node */
'use strict';
const path = require('path');

module.exports = {
  name: 'ticketfly-ui',

  contentFor: function(type) {
    if (type === 'head'){
      return '<link rel="stylesheet" href="https://static.ticketfly.com/fonts/icon/builds/current/stylesheet.css">';
    }
  },

  included: function included(app) {
    this._super.included(app);
    const options = app.options['ember-cli-foundation-6-sass'];

    let consumingApp = app;
    while(consumingApp.app) {
      consumingApp = consumingApp.app;
    }

    const foundationPath = path.join(consumingApp.bowerDirectory, 'foundation-sites', 'scss');
    app.options.sassOptions = consumingApp.options.sassOptions || {};
    app.options.sassOptions.includePaths = app.options.sassOptions.includePaths || [];
    app.options.sassOptions.includePaths.push(foundationPath);
  }
};
